svg.script:
 
    function blueState() {
		if(document.getElementById('bluePart').getAttribute('fill') == '#D3D3D3'){
		  document.getElementById('bluePart').setAttribute('fill','#3953A4');
		} else {
		  document.getElementById('bluePart').setAttribute('fill','#D3D3D3');
		}
		buildQuery()
    }
    function redState() {
		if(document.getElementById('redPart').getAttribute('fill') == '#D3D3D3'){
		  document.getElementById('redPart').setAttribute('fill','#ED2024');
		} else {
		  document.getElementById('redPart').setAttribute('fill','#D3D3D3');
		}
		buildQuery()
    }
    function greenState() {
		if(document.getElementById('greenPart').getAttribute('fill') == '#D3D3D3'){
		  document.getElementById('greenPart').setAttribute('fill','#69BD45');
		} else {
		  document.getElementById('greenPart').setAttribute('fill','#D3D3D3');
		}
		buildQuery()
    }
    function yellowState() {
		if(document.getElementById('yellowPart').getAttribute('fill') == '#A9A9A9'){
		  document.getElementById('yellowPart').setAttribute('fill','#FFF200');
		} else {
		  document.getElementById('yellowPart').setAttribute('fill','#A9A9A9');
		}
		buildQuery()
    }
    function cyanState() {
		if(document.getElementById('cyanPart').getAttribute('fill') == '#A9A9A9'){
		  document.getElementById('cyanPart').setAttribute('fill','#00AEEF');
		} else {
		  document.getElementById('cyanPart').setAttribute('fill','#A9A9A9');
		}
		buildQuery()
    }
    function magentaState() {
		if(document.getElementById('magentaPart').getAttribute('fill') == '#A9A9A9'){
		  document.getElementById('magentaPart').setAttribute('fill','#EC008C');
		} else {
		  document.getElementById('magentaPart').setAttribute('fill','#A9A9A9');
		}
		buildQuery()
    }
    function whiteState() {
		if(document.getElementById('whitePart').getAttribute('fill') == '#7F7F7F'){
		  document.getElementById('whitePart').setAttribute('fill','#FFFFFF');
		} else {
		  document.getElementById('whitePart').setAttribute('fill','#7F7F7F');
		}
		buildQuery()
    }

	function buildQuery(){
		// build the advanced query here!
		if(
		document.getElementById('whitePart').getAttribute('fill') == '#7F7F7F' &&
		document.getElementById('cyanPart').getAttribute('fill') == '#A9A9A9' &&
		document.getElementById('yellowPart').getAttribute('fill') == '#A9A9A9' &&
		document.getElementById('magentaPart').getAttribute('fill') == '#A9A9A9' &&
		document.getElementById('redPart').getAttribute('fill') == '#D3D3D3' &&
		document.getElementById('greenPart').getAttribute('fill') == '#D3D3D3' &&
		document.getElementById('bluePart').getAttribute('fill') == '#D3D3D3'
		){
  		  document.getElementById('query').textContent = 'Please Select the documents you wish to be displayed.';		
		} else {
			var counter = 0;
			document.getElementById('query').textContent = 'Find documents that contain: '
		}
		
		if(document.getElementById('greenPart').getAttribute('fill') != '#D3D3D3'){
		  if (counter != 0){
		    document.getElementById('query').textContent += ' OR '
  		  }
		  document.getElementById('query').textContent += ' "ITEM 1"'
		  counter++
		}

		if(document.getElementById('redPart').getAttribute('fill') != '#D3D3D3'){
		  if (counter != 0){
		    document.getElementById('query').textContent += ' OR '
  		  }
		  document.getElementById('query').textContent += ' "ITEM 2"'
		  counter++
		}

		if(document.getElementById('bluePart').getAttribute('fill') != '#D3D3D3'){
		  if (counter != 0){
		    document.getElementById('query').textContent += ' OR '
  		  }
		  document.getElementById('query').textContent += ' "ITEM 3"'
		  counter++
		}

		if(document.getElementById('yellowPart').getAttribute('fill') != '#A9A9A9'){
		  if (counter != 0){
		    document.getElementById('query').textContent += ' OR '
  		  }
		  document.getElementById('query').textContent += ' "ITEM 1 AND ITEM 2"'
		  counter++
		}

		if(document.getElementById('cyanPart').getAttribute('fill') != '#A9A9A9'){
		  if (counter != 0){
		    document.getElementById('query').textContent += ' OR '
  		  }
		  document.getElementById('query').textContent += ' "ITEM 1 AND ITEM 3"'
		  counter++
		}

		if(document.getElementById('magentaPart').getAttribute('fill') != '#A9A9A9'){
		  if (counter != 0){
		    document.getElementById('query').textContent += ' OR '
  		  }
		  document.getElementById('query').textContent += ' "ITEM 2 AND ITEM 3"'
		  counter++
		}

		if(document.getElementById('whitePart').getAttribute('fill') != '#7F7F7F'){
		  if (counter != 0){
		    document.getElementById('query').textContent += ' OR '
  		  }
		  document.getElementById('query').textContent += ' "ITEM 1 AND ITEM 2 AND ITEM 3"'
		  counter++
		}
		
	}
